$('document').ready(()=>{console.log(foods);let a=$('#food-container');let i;for(i=0;i<foods.length;i++){let A=foods[i].name;let b=foods[i].price;let c=foods[i].pict;let d=foods[i].description;a.append(`
        <div class="food-item card mb-4">
            <img src="${c}">
        
            <div class="container card-body">
                <!-- Food Title -->
                <div class="row mt-3">
                    <div class="col-8">
                        <h4>${A}</h4>
                    </div>
                    
                    <div class="col">
                        <h5>${b}</h5>
                    </div>
                </div>
        
                <div class="row mt-2">
                    <div class="col">
                        <p>${d}</p>
                    </div>
                </div>
            </div>
        </div>`)}});
